import React, { useState } from "react";
import "./RandomNumber.css";

function RandomNumber() {
  const [random, setRandom] = useState(0); // 현재 랜덤 숫자 상태
  const [rolling, setRolling] = useState(false); // 숫자가 굴러가는지 여부

  const startRandomRoll = () => {
    if (rolling) return; // 이미 굴러가는 중이면 다시 시작하지 않도록

    setRolling(true); // 숫자 굴리기 시작
    const interval = setInterval(() => {
      setRandom(Math.floor(Math.random() * 100)); // 숫자가 빠르게 변하게 함
    }, 10); // 100ms마다 숫자 변경

    // 3초 후에 멈추기
    setTimeout(() => {
      clearInterval(interval); // 숫자 굴리는 것을 멈춤
      setRolling(false); // 굴리기 종료
    }, 1000); // 1초동안만
  };

  return (
    <div>
      <h2 className="title">랜덤 숫자 생성기</h2>
      <p className="result">{random}</p>
      <button
        className="random-button"
        onClick={startRandomRoll}
        disabled={rolling}
      >
        Start
        {/* {rolling ? "굴러가는 중..." : "Start"} */}
      </button>
    </div>
  );
}

export default RandomNumber;
