import React, { Component } from "react";

// 클래스형 컴포넌트 : rcc 스니펫
// 상태는 this.state, this.setState
// 생명주기 메서드
// 매초 시계를 업데이트
class Clock extends Component {
  constructor(props) {
    super(props);
    this.state = { date: new Date() };
  }

  // componentDidMount, componentWillUnmount: 기본 제공 메서드
  componentDidMount() {
    // 1초마다 틱 호출
    this.timerId = setInterval(() => this.tick(), 1000); // id 설정
  }

  componentWillUnmount() {
    clearInterval(this.timerId); // 없어질때는 클리어
  }

  tick() {
    // 사용자 정의 함수
    this.setState({
      date: new Date(),
    });
  }
  render() {
    return (
      <div>
        <h2>현재시간 : {this.state.date}</h2>
      </div>
    );
  }
}

export default Clock;
