import React, { useState } from "react"; // {} 작성하지않으면 React.useState, 쓰면 useState

// 상태관리 useState() 사용
// 버튼 클릭해서 클릭수 증가
function Counter(props) {
  const [count, setCount] = useState(0);

  return (
    <div>
      <h3>버튼을 {count}번 클릭했습니다.</h3>
      <button onClick={() => setCount}>클릭</button>
    </div>
  );
}

export default Counter;
